article = (id_article DECIMAL(7,0), prixht_article CURRENCY, nom_article VARCHAR(30), lib_article VARCHAR(30), four_article DECIMAL(3,0), ref_article VARCHAR(20), photo_article VARCHAR(5000));
catalogue = (#(id_article, prixht_article), theme_article VARCHAR(30));
sous_rubrique = (#(id_article, prixht_article), type_article VARCHAR(30));
stock = (id_stock DECIMAL(7,0), min_stock DECIMAL(3,0), max_stock DECIMAL(6,0), reel_stock DECIMAL(6,0));
categorie = (id_cat DECIMAL(7,0), pay_cat VARCHAR(30), coef_cat DECIMAL(2,0), lib_cat VARCHAR(30));
reglement = (id_regl DECIMAL(7,0), date_regl DATE, type_regl VARCHAR(20), total_regl CURRENCY);
categorie_fournisseur = (id_cat_four DECIMAL(7,0), lib_cat_four VARCHAR(30));
commande_fournisseur = (id_com_four DECIMAL(7,0), bl_com_four VARCHAR(5000), num_bl_com_four DECIMAL(15,0), date_com_four DATE, prixht_com_four CURRENCY);
reglement_fournisseur = (id_regl_four DECIMAL(7,0), date_regl_four DATE, type_regl_four VARCHAR(20), total_regl_four CURRENCY);
livraison_four = (id_liv_four DECIMAL(7,0), date_liv_four DATE, num_liv_four DECIMAL(15,0));
client = (id_client DECIMAL(7,0), nom_client VARCHAR(30), prenom_client VARCHAR(30), adresse_client VARCHAR(30), ville_client VARCHAR(20), cp_client VARCHAR(5), #(id_cat, pay_cat, coef_cat));
commande = (id_com DECIMAL(7,0), bl_com VARCHAR(5000), date_com DATE, prixht_com CURRENCY, num_com DECIMAL(15,0), #id_client);
commercial = (id_commercial DECIMAL(3,0), nom_commercial VARCHAR(30), prenom_commercial VARCHAR(30), tel_commercial VARCHAR(10), courriel_commercial VARCHAR(30), #id_client);
fournisseur = (id_four DECIMAL(7,0), nom_four VARCHAR(30), adre_four VARCHAR(30), ville_four VARCHAR(30), cp_four VARCHAR(5), tel_four VARCHAR(10), courriel_four VARCHAR(30), pays_four VARCHAR(30), #id_cat_four);
compter = (#(id_article, prixht_article), #id_stock);
acheter = (#(id_article, prixht_article), #id_client);
commander = (#(id_article, prixht_article), #id_com, id_liste DECIMAL(7,0), quantite_article DECIMAL(4,0), prixht_total_article CURRENCY);
facturer = (#id_client, #id_regl, id_facture DECIMAL(7,0), num_facture DECIMAL(15,0), bl_facture VARCHAR(5000), prix_facture CURRENCY, date_facture DATE);
donner = (#id_commercial, reduc_commercial DECIMAL(2,0), #id_com);
etablir = (#id_four, #id_regl_four, #id_liv_four, id_fact_four DECIMAL(7,0), bl_fact_four VARCHAR(5000), num_bl_fact_four DECIMAL(15,0), prix_fact_four CURRENCY, date_fact_four DATE);
demander = (#id_four, #id_com_four);
envoyer = (#(id_article, prixht_article), #id_com_four, id_liste_article_four DECIMAL(7,0), quantite_article_four DECIMAL(15,0), prixht_total_article_four CURRENCY);
rajouter = (#(id_article, prixht_article), quantite_liv_depot DECIMAL(15,0), nombre_piece_liv_depot DECIMAL(15,0), total_prix_quant_liv_depot CURRENCY, #id_liv_four);
